package com.example.form;import androidx.appcompat.app.AppCompatActivity;import android.app.Activity;import android.content.Intent;import android.view.View;import android.widget.Button;import android.widget.TextView;import android.os.Bundle;public class MainActivity extends AppCompatActivity {    private TextView txtv;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        txtv = (TextView) findViewById(R.id.Registerlink);        txtv.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                openRegisterForm();            }        });    }    public void openRegisterForm()    {        Intent Registerintent = new Intent(this, RegisterForm.class);        startActivity(Registerintent);    }}